mapper.select:
select TABLE_SEQ as tableSeq, TABLE_ID as tableId, TABLE_NAME as tableName, VBG_CRE_USER as vbgCreUser, VBG_CRE_DTM as vbgCreDtm, 0 as dataCount, false as selected from std_main
and ( TABLE_NAME like concat('%', #{search}, '%') or TABLE_ID like concat('%', #{search}, '%') )
and VBG_CRE_DTM >= #{start}
and VBG_CRE_DTM <= #{end}

mapper.delete:
delete from std_main
 where TABLE_SEQ in

     #{item}

delete from std_field
 where TABLE_SEQ = #{tableSeq}
   and COL_ID = #{colId}
